var DEG_TO_RAD = Math.PI / 180.0;
var EARTH_RADIUS = 6371000.0;

function great_circle(a_lat, a_lon, b_lat, b_lon) {
  a = { lat: a_lat, lon: a_lon }
  b = { lat: b_lat, lon: b_lon }
  // degrees to radii
  a.lat = a.lat * DEG_TO_RAD;
  a.lon = a.lon * DEG_TO_RAD;
  b.lat = b.lat * DEG_TO_RAD;
  b.lon = b.lon * DEG_TO_RAD;

  var d_lon = b.lon - a.lon;
  var sa = Math.cos(b.lat) * Math.sin(d_lon);
  var sb = (Math.cos(a.lat) * Math.sin(b.lat)) - (Math.sin(a.lat) * Math.cos(b.lat) * Math.cos(d_lon));
  var aa = Math.sqrt(Math.pow(sa, 2) + Math.pow(sb, 2));
  var ab = (Math.sin(a.lat) * Math.sin(b.lat)) + (Math.cos(a.lat) * Math.cos(b.lat) * Math.cos(d_lon));
  var angle_at_centre = Math.atan2(aa, ab);
  return angle_at_centre * EARTH_RADIUS;
}
function parse_data(data) {
  console.log(data)

  const sLat = data.series[0].fields[1].values;
  const sLon = data.series[1].fields[1].values;
  const sLatTime = data.series[0].fields[0].values;
  const sLonTime = data.series[1].fields[0].values;
  var filtered = []
  for (let i = 0; i < sLat.length; i++) {
    if (sLat[i] && sLon[i]) {
      if (sLatTime[i] === sLonTime[i]) {
        filtered.push([sLatTime[i], sLat[i], sLon[i]])
      }
    } else {
    }
  }
  var calculated = []
  for (let i = 10; i < filtered.length; i++) {
    var figure = Math.abs(
      great_circle(filtered[i][1], filtered[i][2], filtered[i - 10][1], filtered[i - 10][2])
      /
      ((filtered[i][0] - filtered[i - 10][0]) / 1000)
    ) * 3.6
    if (figure < 1000) {
      calculated.push(
        [
          filtered[i][0],
          figure
        ]
      )
    }
  }
  const output = calculated
  return {
    name: 'Speed km/hr',
    type: 'line',
    showSymbol: false,
    areaStyle: {
      opacity: 0.1,
    },
    lineStyle: {
      width: 1,
    },
    data: output,
  };
};

